<script setup>
	import {
		ref,
		provide,
		onMounted,
		getCurrentInstance
	} from 'vue';
	import {
		useRouter,
		useRoute
	} from 'vue-router';
	// 修改为正确的图标导入
	import {
		useCounterStore
	} from '../store/index';
	const store = useCounterStore();
	const router = useRouter();
	const route = useRoute();
	const activeMenu = ref(route.name);

	const tarbarH = ref(60)
	const tabBarRef = ref(null)
	import en from '../locales/en.js';
	import zh from '../locales/zh.js';
	import jp from '../locales/jp.js';
	import ko from '../locales/ko.js';
	import vi from '../locales/vi.js';
	const langs = {
		en,
		zh,
		jp,
		ko,
		vi
	}
	const instance = getCurrentInstance();
	console.log(localStorage.getItem('language'))
	//状态管理仓库的值更行了  其他组件如果调用这个状态值  页面会更新  script会延迟更新
	let lang = ref()
	if (!store.lang) {
		if (!localStorage.getItem('language')) {
			localStorage.setItem('language', instance.appContext.config.globalProperties.$language)
		} else {
			lang.value = langs[localStorage.getItem('language')]
		}
	} else {
		lang.value = langs[store.lang]
	}
	console.log(lang.value)
	import {
		Modal
	} from 'ant-design-vue';
	// const sy = () => {
	// 	const modal = Modal.info({
	// 		title: 'システムメッセージ',
	// 		content: 'バージョンを更新中です…',
	// 		okText: '承知しました',
	// 	});
	// }
	onMounted(() => {
		tarbarH.value = tabBarRef.value.offsetHeight
	})

	const navigateTo = (name) => {
		//私约顶部视频播放
		if(name == 'PL') {
			store.PLplay = true
		} else {
			store.PLplay = false
		}
		router.push({
			name
		});
		activeMenu.value = name;
	};
</script>

